mapper.select:
SELECT u.USERSCODE,u.USERID,u.password as pw
      ,u.name,u.birth,u.phone as phone
      ,u.email,u.gender,u.roles,u.hidden,u.deleted

FROM USERS u

WHERE u.USERID = #{userid}
AND u.password = #{pw}
AND u.deleted = 'N'
AND u.roles = #{roles}

SELECT
USERID, name, phone as phone
FROM USERS
WHERE name = #{name}
AND phone = #{phone}
AND deleted = 'N'

SELECT
userscode,name,USERID,email,password as pw
FROM USERS
WHERE name = #{name}
AND email = #{email}
AND USERID = #{userid}
AND deleted = 'N'

mapper.update:
		 UPDATE USERS
    		SET password = #{pw}
  		  WHERE userid = #{userid}
