mapper.select:
SELECT
USERID, name, phone as phone
FROM USERS
WHERE name = #{name}
AND phone = #{phone}
AND deleted = 'N'

SELECT
name,USERID,email,password as pw
FROM USERS
WHERE name = #{name}
AND email = #{email}
AND USERID = #{userid}
AND deleted = 'N'

mapper.update:
		 UPDATE USERS
    		SET password = #{pw}
  		  WHERE userid = #{userid}
